function isEmailValid(email){var e=new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);return e.test(email);}
function sendForm(){jQuery("#imessageOK, #imessageERROR").hide();jQuery(".required").removeClass("required");if(jQuery("#YourName").val()==""){jQuery("#YourName").addClass("required");window.scroll(0,0);return false;}if(jQuery("#YourEmail").val()==""){jQuery("#YourEmail").addClass("required");window.scroll(0,0);return false;}if(!isEmailValid(jQuery("#YourEmail").val())){jQuery("#YourEmail").addClass("required");window.scroll(0,0);return false;}if(jQuery("#YourFeedback").val()==""){jQuery("#YourFeedback").addClass("required");window.scroll(0,0);return false;}jQuery("#SendaMessage").val("Un momento...");var AddDetails;AddDetails+="&YourName="+encodeURIComponent(jQuery("#YourName").val());AddDetails+="&YourEmail="+encodeURIComponent(jQuery("#YourEmail").val());AddDetails+="&YourFeedback="+encodeURIComponent(jQuery("#YourFeedback").val());jQuery.ajax({type:"POST",url:"izzyFeedback.php",data:AddDetails,success:function(data){if(data=="OK"){jQuery("#imessageERROR").hide();jQuery("#izzyFeedback .iform ul").fadeOut(function(){jQuery("#imessageOK").fadeIn(function(){setTimeout('jQuery("#imessageOK").fadeOut(function(){jQuery("#izzyFeedback .iform ul").fadeIn();jQuery("#SendaMessage").val("Invia un altro Messaggio")});',5000);});});}else{jQuery("#imessageOK").hide();jQuery("#SendaMessage").val("Try again: Send a Message")
jQuery("#imessageERROR").fadeIn(function(){setTimeout('jQuery("#imessageERROR").fadeOut()',5000);});}}});}
$(function(){$("#izzyFeedbackStart").css({'cursor':'pointer'}).click(function(){if($(this).offset().left>20){$("#izzyFeedbackBg").fadeOut();$("#izzyFeedback").animate({left:"-404px",},500);}else{$("#izzyFeedbackBg").fadeIn();$("#izzyFeedback").animate({left:"-10px",},500);}});$("#izzyFeedbackBg").click(function(){$("#izzyFeedbackStart").trigger('click')});$(".izzyFeedbackTab1").click(function(){$("#IzzyHelp").slideUp(function(){$(".iform").slideDown();});$("#izzyFeedbackTab a").removeClass('izzyFeedbackActiveTab');$(this).addClass('izzyFeedbackActiveTab');});$(".izzyFeedbackTab2").click(function(){$(".iform").slideUp(function(){$("#IzzyHelp").slideDown();});$("#izzyFeedbackTab a").removeClass('izzyFeedbackActiveTab');$("#izzyFeedbackTab a.izzyFeedbackTab2").addClass('izzyFeedbackActiveTab');});});